# Set the SMTP Virtual Server object path\n$smtpServer = (Get-WmiObject -Namespace "ROOT\MicrosoftTsm" -Class IIsSmtpServerSetting)\n\n# Disable delivery of non-delivery reports (NDRs)\n$smtpServer.DeliverUndelTo = "None"\n\n# Optionally, set up a smart host to relay emails\n# $smtpServer.SmartHost = "your.smarthost.com"\n# $smtpServer.Save()\n\n# Apply changes\n$smtpServer.SetInfo()