# Open Registry Key for JSP files\n$regPath = "HKEY_CLASSES_ROOT\.jsp\ShellEx\{78D493DD-8AEC-451F-A6B5-C7B3E90AFD25}"\n\n# Set the default value to enable indexing\nSet-ItemProperty -Path $regPath -Name "(default)" -Value "{5e941d80-bf96-11cd-ba2a-00c04fd91a05}" -Type String\n\n# Ensure .jsp is included in the indexed file types\n$indexPath = "HKCU:\Software\Microsoft\Windows\CurrentVersion\Explorer\CIDCap\.jsp"\nIf (!(Test-Path $indexPath)) {\n    New-Item -Path $indexPath\n}\nSet-ItemProperty -Path $indexPath -Name "(default)" -Value 1 -Type DWORD