# Define the thumbprint of your certificate and the account you want to grant access to\n$thumbprint = "YOUR_CERTIFICATE_THUMBPRINT"\n$account = "NT AUTHORITY\NETWORK SERVICE"\n\n# Retrieve the certificate\n$certificate = Get-ChildItem -Path cert:\LocalMachine\My | Where-Object { $_.Thumbprint -eq $thumbprint }\n\n# Use icacls to modify the ACLs of the certificate files\n& icacls.exe "$($certificate.PSParentPath)\$($certificate.Thumbprint).cer" /grant `"$account`:R"