$source = "C:\local\PublishedFolder"\n$destination = "\\remote\path\to\webroot"\n\n# Get a list of all files in source directory\n$sourceFiles = Get-ChildItem $source -Recurse\n\nforeach ($file in $sourceFiles) {\n    # Construct the relative path and destination file path\n    $relativePath = $file.FullName.Substring($source.Length + 1)\n    $destinationFile = Join-Path -Path $destination -ChildPath $relativePath\n    \n    # Check if the destination file exists, is older or different in size (less safe but faster for big files)\n    if (-not (Test-Path $destinationFile) -or \n        ((Get-Item $file.FullName).LastWriteTimeUtc -ne (Get-Item $destinationFile).LastWriteTimeUtc) -or\n        ($file.Length -ne (Get-Item $destinationFile).Length)) {\n        \n        # Copy the file to the destination\n        New-Item -ItemType Directory -Force -Path (Split-Path -Parent $destinationFile)\n        Copy-Item -Path $file.FullName -Destination $destinationFile -Force\n        \n        Write-Host "Updated $relativePath"\n    }\n}