# Import Active Directory module\nImport-Module ActiveDirectory\n\n# Create a new AD group (if not already existing)\nNew-ADGroup -Name "LDAP_Query_Users" -Path "OU=Groups,DC=YourDomain,DC=com" -Description "Users allowed to perform LDAP queries"\n\n# Grant the necessary read permissions for LDAP queries\n$dn = "DC=YourDomain,DC=com"\n$subtree = [System.DirectoryServices.Protocols.SearchScope]::Subtree\n$type = [System.Security.AccessControl.ActiveDirectoryRights]"ReadProperty"\n$objectType = [GUID]"bf967a86-0de6-11d0-a285-00aa003049e2" # guid for "user" object class\n\n$acl = Get-Acl -Path "AD:\$dn"\n$ace = New-Object System.DirectoryServices.ActiveDirectoryAccessRule("CN=LDAP_Query_Users,OU=Groups,DC=YourDomain,DC=com", $type, "Allow", $objectType, $subtree)\n$acl.AddAccessRule($ace)\n\nSet-Acl -Path "AD:\$dn" -AclObject $acl\n\n# Add specific LDAP query account to the group\nAdd-ADGroupMember -Identity "LDAP_Query_Users" -Members "Your_LDAP_Query_Account"