# Load SharePoint Powershell Snapin if not already loaded\nAdd-PSSnapin Microsoft.SharePoint.PowerShell -ErrorAction SilentlyContinue\n\n# Variables for Web URL and List Name\n$webUrl = "http://sitecollection/subsite/"\n$listName = "SurveyList"\n$userGroupName = "YourUserGroup"\n\n# Get the Web and List objects\n$web = Get-SPWeb $webUrl\n$list = $web.Lists[$listName]\n\n# Break role inheritance without copying permissions\n$list.BreakRoleInheritance($false, $true)\n\n# Remove all current user group roles assigned to the list if necessary\nforeach ($roleAssignment in @($list.RoleAssignments | Where-Object {$_.Member.Name -eq $userGroupName}))\n{\n    $list.RoleAssignments.RemoveById($roleAssignment.Member.ID)\n}\n\n# Get Role Definition (e.g., Read)\n$roleDefinition = $web.RoleDefinitions["Read"]\n\n# Create new role assignment\n$group = $web.SiteGroups[$userGroupName]\nif ($group -ne $null) {\n    $roleAssignment = New-Object Microsoft.SharePoint.SPRoleAssignment($group)\n    $roleAssignment.RoleDefinitionBindings.Add($roleDefinition)\n\n    # Add Role Assignment to List\n    $list.RoleAssignments.Add($roleAssignment)\n}\n\n# Update the list\n$list.Update()\n\n# Dispose of web object\n$web.Dispose()