# Import the WebAdministration module which contains the cmdlets we need\nImport-Module WebAdministration\n\n# Define variables for your web app\n$siteName = "Default Web Site"\n$appPath = "/MyWebApp"  # This should start with a slash if it's an application under a site\n$physicalPath = "C:\path\to\your\webapp"\n\n# Check if the site exists, create if not\nif (-Not (Test-Path IIS:\Sites\$siteName)) {\n    New-IISSite -Name $siteName -PhysicalPath $physicalPath -BindingInformation "*:80:"\n}\n\n# Create a web application under the specified site\nNew-WebApplication -Site $siteName -Name "MyWebApp" -PhysicalPath $physicalPath\n\nWrite-Output "Web application created successfully."