# Define the path to your generated AllStruct.cs file\n$filePath = "path\to\your\project\AllStruct.cs"\n\n# Read the content of the file\n$content = Get-Content -Path $filePath -Raw\n\n# Find the position where the last namespace closing brace is and insert Databases struct before it\n$lastNamespaceCloseIndex = [math]::Max($content.LastIndexOf("}"), 0)\n$contentWithDatabasesInNamespace = $content.Insert(\n    $lastNamespaceCloseIndex, \n    "`n`n`tpublic partial struct Databases { ... }"\n)\n\n# Remove the incorrect Databases declaration outside the namespace\n$fixedContent = [regex]::Replace($contentWithDatabasesInNamespace, "public partial struct Databases \s*{\s*\.\.\.\s*}\s*#", "")\n\n# Write the corrected content back to the file\nSet-Content -Path $filePath -Value $fixedContent