# Run these commands in PowerShell as Administrator\n   Install-WindowsFeature AD-Domain-Services -IncludeManagementTools\n   Import-Module ADDSDeployment\n   \n   # Install the First Domain Controller in a New Forest\n   Install-ADDSForest -CreateDnsDelegation:$false -DatabasePath "C:\Windows\NTDS" `\n                      -DomainMode "WinThreshold" -DomainName "testlab.local" `\n                      -DomainNetbiosName "TESTLAB" -ForestMode "WinThreshold" `\n                      -InstallDns:$true -LogPath "C:\Windows\Logs\ADDS" `\n                      -NoRebootOnCompletion:$false -SysvolPath "C:\Windows\SYSVOL"`\n                      -Force:$true