# Import the SQL Server Reporting Services module if not already loaded\nImport-Module SqlServer\n\n# Define your server and path parameters\n$reportServerUri = 'http://yourReportingServer/Reportserver'\n$catalogPath = '/path/to/new/report'\n\n$ssrsItems = @{\n    Path        = "C:\Path\To\Your\Project\Folder"\n    ReportServerUri= $reportServerUri\n    Recurse     = $true\n}\n\n# Deploy the report to the catalog path\nWrite-RsFolderContent @ssrsItems -CatalogPath $catalogPath\n\nWrite-Output "Report deployment completed successfully."