# Load TFS PowerShell module\nImport-Module TfsPowerShell\n\n# Variables\n$serverUrl = "http://your-tfs-server:8080/tfs/DefaultCollection"\n$labelName = "MyUniqueLabel"\n\n# Connect to TFS\n$tfs = New-TfsTeamProjectCollection $serverUrl -UseDefaultCredential\n$tfs.EnsureAuthenticated()\n\n# Get Version Control server\n$vcsServer = $tfs.GetService([Microsoft.TeamFoundation.VersionControl.Client.VersionControlServer])\n\n# Check if label exists\n$existingLabels = $vcsServer.QueryLabels($labelName, [string]::Empty, [string]::Empty, [string]::Empty, [string]::Empty, 0)\nif ($existingLabels -and $existingLabels.Length -gt 0) {\n    Write-Host "Label '$labelName' already exists."\n} else {\n    Write-Host "Label '$labelName' is available. You can create it."\n}