# Set read/write permissions only to specific folders needed by the application\n$folders = @("C:\Path\To\WebApp\App_Data", "C:\Path\To\WebApp\Uploads")\n$account = "NT AUTHORITY\Network Service"\n\nforeach ($folder in $folders) {\n    # Remove all inherited and existing access rules for the folder\n    $acl = Get-Acl -Path $folder\n    $acl.SetAccessRuleProtection($true, $false)\n    \n    # Grant ReadAndWrite rights to Network Service\n    $accessRule = New-Object System.Security.AccessControl.FileSystemAccessRule($account, "ReadAndExecute", "ContainerInherit,ObjectInherit", "None", "Allow")\n    $acl.AddAccessRule($accessRule)\n    \n    # Set the new access control list for the folder\n    Set-Acl -Path $folder -AclObject $acl\n}