# Import the necessary module\nImport-Module -Name Microsoft.PowerShell.Security\n\n# Define the path for HKCR\n$regPath = "Registry::HKEY_CLASSES_ROOT"\n\n# Get the current ACL of the registry key\n$acl = Get-Acl -Path $regPath\n\n# Create a new Access Control Entry (ACE) granting FullControl to Everyone\n$accessRule = New-Object System.Security.AccessControl.RegistryAccessRule ("Everyone", "FullControl", [System.Security.AccessControl.InheritanceFlags]::ContainerInherit -bor [System.Security.AccessControl.InheritanceFlags]::ObjectInherit, [System.Security.AccessControl.PropagationFlags]::None, "Allow")\n\n# Add the access rule to the ACL\n$acl.SetAccessRule($accessRule)\n\n# Set the modified ACL back to the registry key\nSet-Acl -Path $regPath -AclObject $acl\n\nWrite-Output "Permissions have been set for HKCR and all subkeys."