# Load required assemblies if not already loaded\nAdd-Type -AssemblyName Microsoft.Office.Interop.Excel\nAdd-PSSnapin Microsoft.SharePoint.PowerShell -ErrorAction SilentlyContinue\n\n# Variables for Processing\n$WebURL = "http://your-sharepoint-site-url"\n$ListName = "Your SharePoint List Name"\n$ExcelFilePath = "C:\Path\To\Your\ExcelFile.xlsx"\n\n# Get the Web and List objects\n$web = Get-SPWeb $WebURL\n$list = $web.Lists[$ListName]\n\n# Create a COM object for Excel\n$objExcel = New-Object -ComObject Excel.Application \n$objWorkBook = $objExcel.Workbooks.Open($ExcelFilePath)\n$objSheet = $objWorkBook.Sheets.Item(1)\n\n# Get the range of data (assuming data starts from cell A2)\n$rowMax = ($objSheet.UsedRange.Rows).count\n$colMax = ($objSheet.UsedRange.Columns).count\n\n# Loop through each row of data in Excel and add to SharePoint List\nfor ($row=2; $row -le $rowMax; $row++) {\n    $itemCreateInfo = New-Object Microsoft.SharePoint.SPListItemCreationInformation \n    $newItem = $list.AddItem($itemCreateInfo)\n\n    for ($col=1; $col -le $colMax; $col++) {\n        # Assuming column headers in row 1 are the internal names of list columns\n        $columnName = $objSheet.Cells.Item(1, $col).Text \n        if (-Not [string]::IsNullOrEmpty($columnName)) {\n            try {\n                $newItem[$columnName] = $objSheet.Cells.Item($row,$col).text     \n            } catch {\n                Write-Host "Error setting field '$columnName': $_"\n            }\n        }\n    }\n\n    $newItem.Update()\n}\n\n# Cleanup\n$objWorkBook.Close()\n[System.Runtime.InteropServices.Marshal]::ReleaseComObject($objExcel)\n$web.Dispose()\n\nWrite-Host -ForegroundColor Green "Data import completed successfully!"