function Get-VolumeFromPath {\n    param (\n        [string]$Path\n    )\n\n    # Ensure the path ends with a backslash\n    if (-not $Path.EndsWith("\")) {\n        $Path += "\"\n    }\n\n    # Query WMI for all volumes and their mount points\n    $volumes = Get-WmiObject -Class Win32_Volume | Where-Object { $_.DriveLetter -ne $null }\n\n    foreach ($volume in $volumes) {\n        # Check if the path is within this volume\n        if ($Path.StartsWith($volume.DriveLetter + "\")) {\n            return $volume.Name\n        } elseif ($volume.MountPoints) {\n            foreach ($mountPoint in $volume.MountPoints.Split(";")) {\n                if ($mountPoint.Trim().EndsWith("\")) {\n                    if ($Path.StartsWith($mountPoint.Trim())) {\n                        return $volume.Name\n                    }\n                } else {\n                    if ($Path.StartsWith("$($mountPoint.Trim())\")) {\n                        return $volume.Name\n                    }\n                }\n            }\n        }\n    }\n\n    throw "Volume not found for path: $Path"\n}\n\n# Example usage:\n$databaseFilePath = "D:\ExchangeData\DBs\ExampleDB.edb"\n$volumeName = Get-VolumeFromPath -Path $databaseFilePath\nWrite-Output "The database file is on volume: $volumeName"