# Create a SQL query file (query.sql)\n   $sqlQuery = @"\n   SET NOCOUNT ON;\n   SELECT 'Field1', 'Field2', 'Field3'\n   UNION ALL\n   SELECT Column1, Column2, Column3 \n   FROM YourTable;\n   "@\n\n   Set-Content -Path "query.sql" -Value $sqlQuery\n\n   # Run SQLCMD and format the output to CSV with quotes\n   sqlcmd -S YourServerName -d YourDatabaseName -i query.sql -h-1 -s"," | Format-Csv -Delimiter "," -QuoteField *