# Adding a new Application\nNew-IISSite -Name "SampleApp" -PhysicalPath "C:\inetpub\wwwroot\sampleapp" -BindingInformation "*:80:"\nAdd-WebConfigurationProperty -pspath 'IIS:\Sites\SampleApp'  -filter "system.webServer/webApplications" -name "." -value @{path="/";physicalPath="C:\inetpub\wwwroot\sampleapp"}\n\n# Adding a Virtual Directory under the Default Web Site\nNew-WebVirtualDirectory -Site "Default Web Site" -Name "VDirExample" -PhysicalPath "C:\inetpub\vdircontent"