# Path to your .csproj file\n$projectFilePath = "C:\path\to\your\project.csproj"\n\n# Load content of the project file\n$xml = [xml](Get-Content $projectFilePath)\n\n# Select all Exclude attributes in the XML and remove them\n$xml.SelectNodes("//*/Exclude") | ForEach-Object {\n    $_.ParentNode.RemoveAttribute("Exclude")\n}\n\n# Save changes back to the same .csproj file\n$xml.Save($projectFilePath)