# Load SharePoint Client Assemblies\nAdd-Type -Path "C:\Program Files\Common Files\Microsoft Shared\Web Server Extensions\16\ISAPI\Microsoft.SharePoint.Client.dll"\nAdd-Type -Path "C:\Program Files\Common Files\Microsoft Shared\Web Server Extensions\16\ISAPI\Microsoft.SharePoint.Client.Runtime.dll"\n\n# Define parameters\n$url = "https://your-site-url/"\n$username = "your-username@domain.com"\n$password = ConvertTo-SecureString "your-password" -AsPlainText -Force\n$credential = New-Object Microsoft.SharePoint.Client.SharePointOnlineCredentials($username, $password)\n\n# Create context\n$clientContext = New-Object Microsoft.SharePoint.Client.ClientContext($url)\n$clientContext.Credentials = $credential\n\n# Load all lists in the web\n$web = $clientContext.Web\n$lists = $web.Lists\n$clientContext.Load($web)\n$clientContext.Load($lists)\n$clientContext.ExecuteQuery()\n\n# Variable to store changes summary\n$summaryReport = "Changes Summary for $url`n"\n\nforeach ($list in $lists) {\n    # Query the list items with a date range for changes (example: last day)\n    $query = New-Object Microsoft.SharePoint.Client.CamlQuery\n    $dayBefore = (Get-Date).AddDays(-1).ToString("yyyy-MM-dd")\n    $camlQuery = "<View>\n                    <Where><And>\n                        <Neq><FieldRef Name='Editor'/><Value Type='User'><UserID Type='Integer'/></Value></Neq>\n                       <Geq><FieldRef Name='Modified'/><Value IncludeTimeValue='FALSE' Type='DateTime'>$dayBefore</Value></Geq>\n                    </And></Where>\n                 </View>"\n    $query.ViewXml = $camlQuery\n    $itemCollection = $list.GetItems($query)\n    $clientContext.Load($itemCollection)\n    $clientContext.ExecuteQuery()\n\n    foreach ($item in $itemCollection) {\n        $summaryReport += "List: $($list.Title) - Item Modified by: $($item["Editor"]), ID: $($item.Id), Modified on: $($item["Modified"])`n"\n    }\n}\n\n# Send email with the summary\nSend-MailMessage -From "no-reply@domain.com" -To "recipient@domain.com" -Subject "Daily Changes Summary" -Body $summaryReport -SmtpServer "smtp.yourserver.com"