# Import WebAdministration module\nImport-Module WebAdministration\n\n# Get the website and application pool to check their configurations\n$siteName = "TfsSite"\n$webSitePath = "IIS:\Sites\$siteName"\n\n# Check Authentication settings\nGet-WebConfigurationProperty -filter "/system.webServer/security/authentication/anonymousAuthentication" -pspath $webSitePath -name "enabled"\nGet-WebConfigurationProperty -filter "/system.webServer/security/authentication/windowsAuthentication" -pspath $webSitePath -name "enabled"