# Define the path where COM registered .NET assemblies are listed\n$comRegisteredAssembliesPath = "HKLM:\SOFTWARE\Classes\CLSID"\n\n# Get all items under CLSID to find those with a 'InprocServer32' or 'LocalServer32'\nGet-ChildItem -Path $comRegisteredAssembliesPath | ForEach-Object {\n    $server32 = Join-Path $_.PsPath "InprocServer32"\n    if (Test-Path $server32) {\n        # Check the default value for .NET path\n        $clsidDefault = Get-ItemProperty -Path $server32\n        if ($_.PSChildName -and $clsidDefault.'(Default)' -like "*mscoree.dll*") {\n            Write-Host "Assembly registered with GUID: $($_.PSChildName) is located at: $($clsidDefault.'(Default)')"\n            \n            # Get the .NET assembly information\n            $programIdPath = "$($comRegisteredAssembliesPath)\$($_.PSChildName)\Programmable"\n            if (Test-Path $programIdPath) {\n                $assemblyInfo = Get-ItemProperty -Path $programIdPath\n                Write-Host "Assembly Path: $($assemblyInfo.'(Default)')"\n            }\n        }\n    }\n}