# Run this from your local PowerShell session to set properties of a virtual directory on a remote server\n$remoteComputer = "RemoteServerName"\n$siteName = "Default Web Site"\n$vdirPath = "MyVirtualDirectory"\n$newPhysicalPath = "C:\inetpub\wwwroot\NewLocation"\n\nInvoke-Command -ComputerName $remoteComputer -ScriptBlock {\n    Import-Module WebAdministration\n    Set-ItemProperty IIS:\Sites\$using:siteName\$using:vdirPath -name physicalPath -value $using:newPhysicalPath\n}