check process apache with pidfile /var/run/apache2.pid\n      start program = "/etc/init.d/apache2 start"\n      stop program  = "/etc/init.d/apache2 stop"\n\n   check url apache_status with timeout 15 seconds\n       protocol http\n       host "localhost"\n       port 80\n       content == "Total Accesses"    # Adjust this as needed based on server-status output\n       if failed for 3 times within 2 cycles then alert\n       if succeeded after 2 cycles then exec "/usr/local/bin/monit-service-recovered apache"