#!/bin/ksh\n\n# Directory containing the files\nDIR="/path/to/your/files"\n\n# Find files modified in the last 7 days, sort them by modification time and day\nfind "$DIR" -maxdepth 1 -type f -mtime -7 | \\nawk '{\n    cmd="stat --format=\%\nY " $0;       # Use stat to get the modification time\n    cmd | getline secs;\n    close(cmd);\n    day=secs/(60*60*24);                  # Convert seconds to days since epoch\n    if (dayfile[day]=="" || int(secs) > int(day_sec[day])) {\n        dayfile[day]=$0;                    # Store the file name\n        day_sec[day]=secs;                  # Store the modification time in seconds\n    }\n}\nEND {\n    for (d in dayfile)\n        print dayfile[d]                    # Print the most recent file per day\n}' | sort -r                                # Sort output to list from newest to oldest day