# Determine architecture or use an environment variable\nSYSTEM_TYPE := $(shell uname -m)\n\nifeq ($(SYSTEM_TYPE), x86_64)\n    INCLUDE_PATH = -I$(HOME)/libraries/common/include\n    LIBRARY_PATH = -L$(HOME)/libraries/common/lib\nelse\n    # Add more conditions for other system types if necessary\n    INCLUDE_PATH = -I$(HOME)/libraries/arch1/include\n    LIBRARY_PATH = -L$(HOME)/libraries/arch1/lib\nendif\n\nCFLAGS = $(INCLUDE_PATH)\nLDFLAGS = $(LIBRARY_PATH) -lmylib\n\nall: myprogram\n\nmyprogram: main.o\n	$(CC) $(CFLAGS) $^ -o $@ $(LDFLAGS)\n\nmain.o: main.c\n	$(CC) $(CFLAGS) -c $< -o $@