%:\n	dh $@ --with your_additions_here\n\noverride_dh_install:\n	# Example: Setting variables\n	DEB_VERSION = $(shell dpkg-parsechangelog | grep Version: | cut -d' ' -f2)\n	NAME = your_package_name\n	LIBDIR = /usr/lib/$$(dpkg-archquery --debian-architecture '$(debhost)' --query /lib)\n\n	# Making the symlink\n	cd debian/your_package_name/usr/bin && ln -sf $(LIBDIR)/$(NAME)-$(DEB_VERSION)/$(NAME) .