# Define compiler and flags\nCC = gcc\n\n# Common flags for both debug and release\nCOMMON_FLAGS = -Wall -Wextra\n\n# Debug-specific flags\nDEBUG_FLAGS = -g -DDEBUG\n\n# Release-specific flags\nRELEASE_FLAGS = -O2\n\n# Default is to build a debug version with symbols enabled\nall: debug\n\n# Target to build the debug version\ndebug:\n	$(CC) $(COMMON_FLAGS) $(DEBUG_FLAGS) -o my_program_debug main.c\n\n# Target to build the release version\nrelease:\n	$(CC) $(COMMON_FLAGS) $(RELEASE_FLAGS) -o my_program_release main.c\n\n# Clean up generated files\nclean:\n	rm -f *.o my_program_debug my_program_release