# Include PSPSDK Makefiles\nPSPSDK=$(pspdksj)\ninclude $(PSPSDK)/lib/build.mak\n\n# Compiler\nCC=pspdksj-gcc\n\n# Example source files\nSOURCES = main.c pjSIP_module.c\n\nOBJECTS = $(SOURCES:.c=.o)\n\nTARGET=sip_on_psp\nTARGET_ELF=$(TARGET).elf\nTARGET_PBP=$(TARGET).pbp\n\nall: $(TARGET_ELF) $(TARGET_PBP)\n\n$(TARGET_ELF): $(OBJECTS)\n	$(CC) -o $@ $^ $(LDFLAGS)\n\n# Convert ELF to PBP for running on PSP:\n$(TARGET_PBP): $(TARGET_ELF)\n	makelcf.pl\n	pack-pbp -f param.sfo eboot.bin=$(TARGET).elf icon0.png icon1.png pic0.png snd0.at3 $(TARGET).pbp\n\nclean:\n	rm -vf $(OBJECTS) $(TARGET_ELF) $(TARGET_PBP)\n\n%.o: %.c\n	$(CC) -c $< -o $@