# Load the X509Certificate2 class\nAdd-Type -AssemblyName System.Security\n\n# Path to your certificate file or store location\n$certPath = "CERT:\LocalMachine\My\<Thumbprint>"\n\n# Get the certificate\n$certificate = Get-Item $certPath\n\n# Access the extensions of the certificate\nforeach ($extension in $certificate.Extensions) {\n    if ($extension.Oid.Value -eq "1.2.840.113583.1.1.9") { # OID for the Description field\n        $extensionData = [System.Convert]::FromBase64String($extension.RawData)\n        $description = [System.Text.Encoding]::UTF8.GetString($extensionData, 2, $extensionData.Length - 2)\n        Write-Output "Description: $description"\n    }\n}