# Add the following code to your ~/.bashrc or equivalent shell configuration file\n\nSSH_ENV="$HOME/.ssh/environment"\n\nfunction start_agent {\n    echo "Starting ssh-agent..."\n    # Start ssh-agent and save the environment variables\n    eval "$(ssh-agent -s)" > "$SSH_ENV"\n    chmod 600 "$SSH_ENV"\n}\n\n# Source SSH settings, if applicable\nif [ -f "$SSH_ENV" ]; then\n     . "$SSH_ENV" > /dev/null\n     # Check whether it's running\n     ps -C ssh-agent | grep ssh-agent > /dev/null || {\n         start_agent;\n     }\nelse\n    start_agent;\nfi\n\nunset SSH_ENV