#! /bin/sh\n\nnbqfile=$1   \nnbqbase=$(basename "$nbqfile" nbq)\ntaglistfiletemp="${nbqbase}taglist_temp"\ntaglistfile="${nbqbase}taglist"\n\n./myccode "$nbqfile" | \nsort | \nuniq -c | \nawk '{print $2}' > "$taglistfiletemp"\n\nnoftags=$(wc -l < "$taglistfiletemp")\necho "$noftags"\n\n# Insert the number of tags at the beginning of taglistfile\nsed "1i\\$noftags" "$taglistfiletemp" > "$taglistfile"