# Create TeamCity VM\nNew-VM -Name "TeamCity" -MemoryStartupBytes 4GB -BootDevice VHD -VHDPath "C:\VMs\TeamCity.vhdx" -VHDSizeBytes 50GB -SwitchName "YourVirtualNetSwitch"\n\n# Create SQL Server for TeamCity VM\nNew-VM -Name "SqlServerTC" -MemoryStartupBytes 2GB -BootDevice VHD -VHDPath "C:\VMs\SqlServerTC.vhdx" -VHDSizeBytes 50GB -SwitchName "YourVirtualNetSwitch"\n\n# Create SVN Repository VM\nNew-VM -Name "SVNRepo" -MemoryStartupBytes 1GB -BootDevice VHD -VHDPath "C:\VMs\SVNRepo.vhdx" -VHDSizeBytes 20GB -SwitchName "YourVirtualNetSwitch"\n\n# Create Build Agents\nfor ($i = 1; $i -le 3; $i++) {\n    New-VM -Name "BuildAgent$i" -MemoryStartupBytes 4GB -BootDevice VHD -VHDPath "C:\VMs\BuildAgent$i.vhdx" -VHDSizeBytes 50GB -SwitchName "YourVirtualNetSwitch"\n}