# Define the chunk size (e.g., 1MB)\nchunk_size=1M\n\n# Use dd to copy data in chunks, appending it to the original file\nwhile [ -s file2 ]; do\n    dd if=file2 of=file1 conv=notrunc oflag=append bs=$chunk_size count=1\n    # Remove the chunk that was just written from file2\n    truncate -s -$chunk_size file2\ndone\n\n# Finally, remove file2 as it should be empty by now\nrm file2