$FooConfig = [xml](Get-Content .\Foo.exe.config -ErrorAction:Stop)\n\n# Use XPath to select the baseAddress node\n$baseAddressNode = $FooConfig.SelectSingleNode("/configuration/system.serviceModel/services/service/host/baseAddresses/add[@baseAddress]")\n\nif ($baseAddressNode) {\n    # Get the current value\n    $currentValue = $baseAddressNode.GetAttribute("baseAddress")\n    Write-Output "Current Base Address: $currentValue"\n\n    # Update the attribute with a new baseAddress\n    $baseAddressNode.SetAttribute("baseAddress", "http://newaddress:9090/Foo")\n\n    # Save the updated XML back to the file\n    $FooConfig.Save("Foo.exe.config")\n}