# Define parameters\n$webSiteName = "Default Web Site/MyApp"\n$sourcePath = "c:\path\to\myapp\build\output"\n$destinationPath = "c:\inetpub\wwwroot\myapp"\n$backupPath = "c:\backups\myapp"\n\n# Backup current website\nWrite-Host "Backing up existing application to $backupPath..."\nMove-Item -Path $destinationPath -Destination $backupPath -Force\n\n# Copy new files\nWrite-Host "Deploying new application..."\nNew-Item -ItemType Directory -Force -Path $destinationPath # Ensure destination exists\nCopy-Item -Path "$sourcePath\*" -Destination $destinationPath -Recurse -Force\n\n# Recycle IIS Application Pool (if necessary)\nWrite-Host "Recycling AppPool for $webSiteName..."\nRestart-WebAppPool -Name $webSiteName\n\nWrite-Host "Deployment complete."