# Check current .html file association\n(Get-ItemProperty -Path 'Registry::HKEY_CLASSES_ROOT\.html').'(default)'\n\n# Set Firefox as the default browser for .html files (replace with your actual path)\n$firefoxPath = "C:\Program Files\Mozilla Firefox\firefox.exe"\n$newCommand = "`"$firefoxPath`" `"%1`""\nSet-ItemProperty -Path 'Registry::HKEY_CLASSES_ROOT\FirefoxURL\shell\open\command' -Name '(default)' -Value $newCommand