# Define the path to your ASP.NET project directory\n$projectPath = "C:\path\to\your\aspnetproject"\n\n# Get all .aspx and .ascx files in the project directory and its subdirectories\n$textFiles = Get-ChildItem -Path $projectPath -Recurse -Include *.aspx,*.ascx\n\nforeach ($file in $textFiles) {\n    # Read content from file using Windows-1251 encoding\n    $content = [IO.File]::ReadAllText($file.FullName, [System.Text.Encoding]::GetEncoding(1251))\n    \n    # Write content back to the same file using UTF-8 encoding without BOM\n    [System.IO.File]::WriteAllText($file.FullName, $content, [System.Text.Encoding]::UTF8)\n}\n\nWrite-Output "Conversion completed."