# Create a new Scheduled Task using PowerShell\n\n# Define the action: Run ROBOCOPY for folder replication\n$action = New-ScheduledTaskAction -Execute 'robocopy' -Argument '\\SourceServer\Share \\TargetServer1\Share /MIR /ZB /NDL /LOG:C:\Logs\robocopy.log'\n\n# Set the trigger: Daily at 3 AM\n$trigger = New-ScheduledTaskTrigger -Daily -At 3am\n\n# Register the task with a name and description\nRegister-ScheduledTask -Action $action -Trigger $trigger -TaskName 'DailyFileReplication' -Description 'Robocopy daily replication of files between servers'