# This is a conceptual script not executable in MOSS 2007 without appropriate adaptations to PowerShell for SharePoint.\n$siteUrl = "http://yourSite"\n$web = Get-SPWeb $siteUrl\n$userGroup = "YourGroupName"\n\n# Check permissions of a specific group\nforeach ($roleAssignment in $web.RoleAssignments) {\n    if ($roleAssignment.Member.Name -eq $userGroup) {\n        foreach ($roleDefinitionBinding in $roleAssignment.RoleDefinitionBindings) {\n            Write-Output "$($roleAssignment.Member.Name) has $($roleDefinitionBinding.Name)"\n        }\n    }\n}