# Load the TFS client assembly\nAdd-Type -Path "C:\Program Files\Common Files\microsoft shared\Team Foundation Server 15.0\Client\Microsoft.TeamFoundation.Client.dll"\nAdd-Type -Path "C:\Program Files\Common Files\microsoft shared\Team Foundation Server 15.0\Client\Microsoft.TeamFoundation.VersionControl.Client.dll"\n\n# Connect to TFS\n$tfs = [Microsoft.TeamFoundation.Client.TfsTeamProjectCollectionFactory]::GetTeamProjectCollection("http://your-tfs-server:8080/tfs/DefaultCollection")\n$vcService = $tfs.GetService([Microsoft.TeamFoundation.VersionControl.Client.VersionControlServer])\n\n# Retrieve changesets by label\n$labelName = "YourLabelName"\n$foundLabels = $vcService.QueryLabels($labelName, $null, $null, $true)\n\nforeach ($label in $foundLabels) {\n    Write-Host "Label: $($label.Name)"\n    foreach ($change in $label.ChangesetIds) {\n        Write-Host "Changeset ID: $change"\n    }\n}