# Replace 'YourProcessName' with the name of your process\n$processName = "YourProcessName"\nGet-WmiObject -Query "SELECT * FROM Win32_Process WHERE Name='$processName'" | ForEach-Object {\n    $dependencies = $_.DependentServices\n    if ($dependencies) {\n        foreach ($dep in $dependencies) {\n            Get-WmiObject -Class Win32_Service -Filter "Name='$dep'" | Select-Object DisplayName, PathName\n        }\n    } else {\n        Write-Host "$processName does not have any dependent services."\n    }\n}