# Create new application pools\nNew-WebAppPool -Name "WebService1_Pool"\nNew-WebAppPool -Name "WebService2_Pool"\n\n# Assign each web service to its own pool\nSet-ItemProperty -Path 'IIS:\Sites\Default Web Site\WebService1' -Name "applicationPool" -Value "WebService1_Pool"\nSet-ItemProperty -Path 'IIS:\Sites\Default Web Site\WebService2' -Name "applicationPool" -Value "WebService2_Pool"