# Define local path, remote path, and WinSCP session options\n$localPath = "C:\path\to\your\build"\n$remotePath = "/webroot/path/to/destination"\n$ftpHost = "ftp.example.com"\n$username = "your_username"\n$password = "your_password"\n\n# Load WinSCP .NET assembly\nAdd-Type -Path "WinSCPnet.dll"\n\n# Setup session options\n$sessionOptions = New-Object WinSCP.SessionOptions\n$sessionOptions.Protocol = [WinSCP.Protocol]::Sftp\n$sessionOptions.HostName = $ftpHost\n$sessionOptions.UserName = $username\n$sessionOptions.Password = $password\n$sessionOptions.SshHostKeyFingerprint = "ssh-rsa 2048 xx:xx..... (optional but recommended for security)"\n\n# Open session and transfer files\n$session = New-Object WinSCP.Session\ntry {\n    $session.Open($sessionOptions)\n    # Synchronize local directory to remote\n    $synchronizationResult = $session.SynchronizeDirectories(\n        [WinSCP.SyncMode]::Remote, \n        $localPath, \n        $remotePath, \n        $False)\n    \n    foreach ($transfer in $synchronizationResult.Transfers) {\n        if (!$transfer.Error -and $transfer.ChangeType -ne [WinSCP.SynchronizationChangeType]::Nothing) {\n            Write-Host ("Upload of {0} succeeded" -f $transfer.FileName)\n        }\n        elseif ($transfer.Error) {\n            Write-Host ("Upload of {0} failed: {1}" -f $transfer.FileName, $transfer.Error)\n        }\n    }\n}\nfinally {\n    # Disconnect\n    $session.Dispose()\n}