# Example PowerShell script to gather versions\n$outputPath = "build_versions.txt"\n\n"System Information:" | Out-File $outputPath -Append\nsysteminfo | findstr /B /C:"OS Name" /C:"OS Version" | Out-File $outputPath -Append\n\n"Visual Studio Versions:" | Out-File $outputPath -Append\nGet-ItemProperty HKLM:\Software\Microsoft\VisualStudio\Setup\State | ForEach {\n    $_.Children | Get-ItemProperty -Name InstallationPath, DisplayName, ProductDisplayVersion | Out-File $outputPath -Append\n}\n\n"Git Version:" | Out-File $outputPath -Append\ngit --version | Out-File $outputPath -Append\n\n# Add other tools as needed...