# Define the SQL Server instance name\n$InstanceName = "YourInstanceName"\n\n# Get the service display name for SQL Server Agent (adjust if using default instance)\nif ($InstanceName -eq "MSSQLSERVER") {\n    $ServiceDisplayName = "SQLServerAgent"\n} else {\n    $ServiceDisplayName = "SQLAgent$" + $InstanceName\n}\n\n# Start the SQL Server Agent Service\n$agentService = Get-Service -Displayname $ServiceDisplayName\n\nif ($agentService.Status -ne 'Running') {\n    try {\n        Start-Service -InputObject $agentService\n        Write-Host "SQL Server Agent service started successfully."\n    } catch {\n        Write-Error "Failed to start SQL Server Agent service: $_"\n    }\n} else {\n    Write-Host "SQL Server Agent service is already running."\n}