# Remove ASP.NET handler for .asp files\nRemove-WebConfigurationProperty `\n    -pspath 'IIS:\Sites\YourSiteName' `\n    -filter "system.webServer/handlers" `\n    -name "." `\n    -AtElement @{name='PageHandlerFactory-Integrated'}\n\n# Add classic ASP handler\nAdd-WebConfigurationSystemCollectionElement `\n    -PSPath 'iis:\sites\YourSiteName' `\n    -CollectionName "handlers" `\n    -Value @{"name"="ClassicASP";"path"="*.asp";"verb"="*";"modules"="IsapiModule";"scriptProcessor"%(windir)\system32\inetsrv\asp.dll"";"resourceType"="Unspecified"}\n\n# Restart IIS\nRestart-Service W3SVC