# Load SQL Server module\nImport-Module SqlServer\n\n# Define the server instance name and database\n$serverInstance = 'YourServer\SQLExpress'\n$databaseName = 'YourDatabaseName'\n\n# Define output path for script file\n$outputFilePath = "C:\path_to_your_script\YourDatabase_Script.sql"\n\n# Backup SQL Database to a Script file\nBackup-SqlDatabase -ServerInstance $serverInstance -Database $databaseName -Script | Out-File -FilePath $outputFilePath -Force\n\nWrite-Host "Database schema and data scripted successfully."