# Example to test SMTP using PowerShell with SSL/TLS\n$smtpServer = "smtp.yourserver.com"\n$port = 587 # or 465 for SSL\n$emailFrom = "sender@yourdomain.com"\n$emailTo = "receiver@otherdomain.com"\n$subject = "Test Email"\n$body = "This is a test email sent using PowerShell and TLS."\n\nSend-MailMessage -SmtpServer $smtpServer `\n                 -Port $port `\n                 -UseSsl `\n                 -From $emailFrom `\n                 -To $emailTo `\n                 -Subject $subject `\n                 -Body $body