#!/bin/bash\n\n# URL of the application to check\nAPPLICATION_URL="http://your-application-url/health-check"\n\n# Email settings\nEMAIL_SUBJECT="Application Health Check Alert"\nEMAIL_TO="team@example.com"\nEMAIL_MESSAGE="Your application might be down or having issues."\n\n# Perform HTTP GET request and capture response code\nHTTP_STATUS=$(curl -sL -o /dev/null -w "%{http_code}" "$APPLICATION_URL")\n\n# Check if the response code indicates a failure (e.g., 4xx, 5xx)\nif [[ $HTTP_STATUS -ge 400 ]]; then\n    echo "Health check failed with status: $HTTP_STATUS" | mail -s "$EMAIL_SUBJECT" "$EMAIL_TO"\nelse\n    echo "Health check passed with status: $HTTP_STATUS"\nfi\n\n# Optionally, add log monitoring here or integrate with a bigger monitoring system