#!/bin/bash\n\n# List of proxy servers from different geographic locations\nproxies=(\n    "http://proxy-us.example.com:8080"\n    "http://proxy-eu.example.com:8080"\n    "http://proxy-asia.example.com:8080"\n)\n\nurl="http://yourwebsite.com"\n\nfor proxy in "${proxies[@]}"; do\n    echo "Testing from $proxy..."\n    curl -x "$proxy" -s -w "@curl-format.txt" -o /dev/null "$url"\ndone